mapper.select:
WITH RECURSIVE role_ancestors AS ( SELECT id, role_name, parent_id, 0 AS deps, CAST(id AS CHAR(1000)) AS path -- 初始化路径，记录已访问的节点ID FROM system_role WHERE id = #{roleId} -- 指定起始角色ID
UNION ALL
SELECT r.id, r.role_name, r.parent_id, ra.deps + 1, CAST(CONCAT(ra.path, ',', r.id) AS CHAR(1000)) -- 将当前节点ID加入路径 FROM system_role r INNER JOIN role_ancestors ra ON r.id = ra.parent_id WHERE FIND_IN_SET(r.id, ra.path) = 0 -- 关键防环条件：检查新节点是否已在路径中 ) SELECT * FROM role_ancestors;

WITH RECURSIVE role_ancestors AS ( SELECT id, role_name, parent_id, 0 AS deps, CAST(id AS CHAR(1000)) AS path, -- 初始化路径，记录已访问的节点ID id as root_id -- 新增：记录初始角色ID FROM system_role WHERE id IN -- 改为IN条件
#{item}
UNION ALL
SELECT r.id, r.role_name, r.parent_id, ra.deps + 1, CAST(CONCAT(ra.path, ',', r.id) AS CHAR(1000)), -- 将当前节点ID加入路径 ra.root_id -- 递归过程中，root_id保持不变 FROM system_role r INNER JOIN role_ancestors ra ON r.id = ra.parent_id WHERE FIND_IN_SET(r.id, ra.path) = 0 -- 防环条件 ) SELECT * FROM role_ancestors;

